mapper.select:
select d.id, d.domain, IF(d.type = 2, concat(d.domain,'.',st.server_addr), concat(st.server_addr,':',d.domain)) as domainFull , d.type, d.status, d.due_time as dueDateTime
from t_domain d left join t_server_tunnel st on st.id = d.server_tunnel_id
where d.user_id = #{userId}

select d.id, IF(d.type = 2, concat(d.domain,'.',st.server_addr), concat(st.server_addr,':',d.domain)) as domain , d.type, d.status, d.original_price,d.sales_price, st.country tunnelCountry, st.area tunnelArea, st.name tunnelName, st.broadband tunnelBroadband from t_domain d left join t_server_tunnel st on st.id = d.server_tunnel_id
d.status = 0 and d.user_id is null
and d.type = #{params.type}
and d.server_tunnel_id = #{params.serverTunnelId}
and d.domain like concat(#{params.keyword},'%')

select d.*,concat(d.domain,'.',st.server_addr) as domainFull from t_domain d
left join t_server_tunnel st on st.id = d.server_tunnel_id
where d.id = #{domainId}
