mapper.update:
update t_domain d , user_package_rights upr set
	d.due_time = #{endTime}
where upr.resource_id = d.id and upr.resource_type in (1,2)
  and d.user_id = upr.user_id
  and d.user_id = #{userId}

mapper.select:
select d.id, IF(d.type = 2, concat(d.domain,'.',st.server_addr), concat(st.server_addr,':',d.domain)) as domain , d.type, d.status, d.original_price,d.sales_price, st.country tunnelCountry, st.area tunnelArea, st.name tunnelName, st.broadband tunnelBroadband from t_domain d left join t_server_tunnel st on st.id = d.server_tunnel_id
d.status = 0 and d.user_id is null
and d.type = #{params.type}
and d.server_tunnel_id = #{params.serverTunnelId}
and d.domain like concat(#{params.keyword},'%')

select d.id, d.domain, IF(d.type = 2, concat(d.domain,'.',st.server_addr), concat(st.server_addr,':',d.domain)) as domainFull , d.type, d.status, st.name serverTunnelName, d.server_tunnel_id, d.create_time, d.buy_time, d.due_time, d.user_id, (select concat(d2.name,'|',d2.deviceId) from t_device d2 left join t_device_mapping dm on d2.id = dm.device_id left join t_domain dom on dm.domain = dom.domain and dom.server_tunnel_id = dm.server_tunnel_id where dom.id = d.id) as deviceInfo from t_domain d left join t_server_tunnel st on st.id = d.server_tunnel_id
and d.domain like concat('%', #{params.domain},'%')
and d.server_tunnel_id = #{params.serverTunnelId}
and d.user_id = #{params.userId}
and d.status = #{params.status}
and d.type = #{params.type}
